                        "strawberry juice, น้ำสตรอเบอร์รี่คั้น => น้ำสตรอเบอร์รี่คั้น, strawberry juice"
                    ]
                }
            },
            "analyzer": {
                "synonyms_thai": {
                    "tokenizer": "thai",
                    "filter": [
                        "lowercase",
                        "my_synonym_filter"
                    ]
                },
                "tag_stop": {
                    "type": "stop",
                    "stopwords": [",", ":", "|"]
                  }
            }
        }
    }
}

